const apiKey = "AIzaSyAXwvRI2RwwVrYClXEKAfpvhnJdB8Ea4NE";
const videoIds = ["fWY8c1_8B_M&list", "lsNuF0YbTEo&list", "3tisOnOkwzo"];

// <-------Fetch and display videos------------> 
async function fetchVideos() {

    const containers = [
        document.getElementById("resource1"),
        document.getElementById("resource2"),
        document.getElementById("resource3")
    ];

    for (let i = 0; i < videoIds.length; i++) {
        const url = `https://www.googleapis.com/youtube/v3/videos?id=${videoIds[i]}&part=snippet,contentDetails,statistics&key=${apiKey}`;
        const response = await fetch(url);
        const data = await response.json();
        if (data.items && data.items.length > 0) {
            displayVideo(data.items[0], containers[i]);
        } else {
            alert("No video details found.");
        }
    }
}

function displayVideo(video, container) {
    const iframe = document.createElement("iframe");
    iframe.src = `https://www.youtube.com/embed/${video.id}`;
    iframe.allowFullscreen = true;
    const title = document.createElement("h3");
    title.textContent = video.snippet.title;
    container.appendChild(iframe);
    container.appendChild(title);
}

//  function to load videos on page ------>
fetchVideos();
